var UIModals = function() {
    var a = function() {
        $.fn.modalmanager.defaults.resize = !0, $.fn.modal.defaults.spinner = $.fn.modalmanager.defaults.spinner = '<div class="loading-spinner" style="width: 200px; margin-left: -100px;"><div class="progress progress-striped active"><div class="progress-bar" style="width: 100%;"></div></div></div>';
        var a = $("#ajax-modal");
        $(".gs_ajax_modal").on("click", function() {
            var path = $(this).attr("path");
            $("body").modalmanager("loading"), setTimeout(function() {
                a.load(path, "", function() {
                    a.modal()
                })
            }, 1e3)
        }), a.on("click", ".update", function() {
            a.modal("loading"), setTimeout(function() {
                a.modal("loading").find(".modal-body").prepend('<div class="alert alert-info fade in">Updated!<button type="button" class="close" data-dismiss="alert">&times;</button></div>')
            }, 1e3)
        })
    };
    return {
        init: function() {
            a()
        }
    }
}();
